<script>
function edit_guarantee(no){
	//alert(no);
	$.ajax({
		type: "POST",
		url: "guarantee_data2.php",
		cache: false,
		data: "contract_no="+no,
		success: function(msg){
					//alert(msg);
					$("#output_guarantee").html(msg);
					$("#output_guarantee").show();
					//alert(msg);
				}
	});
}

function del_guarantee(no){
	//alert(no);
	 $("#dialog_delete_text").html(no);
	 $('#dialog_delete_guarantee').dialog({
         autoOpen: false,
         modal: true,
         hide: 'slide',
         show: 'slide',
         width:'500',
         height: '200',
         buttons: {
		 	ลบ: function() {
               $.ajax({
					type: "POST",
					url: "guarantee_delete.php",
					cache: false,
					data: "contract_no="+no,
					success: function(msg){
						$('#dialog_delete_guarantee').dialog('close');
						change_data('guarantee_data.php','../images/head2/work_data2/guarantee.png');
					}
				});
            },
            ปิด: function() {
               $(this).dialog('close');
            }
         }
      });
	  
	  $('#dialog_delete_guarantee').dialog('open');
}



</script>